from web3 import Web3

def computeAddress(_address, _salt, _hash_bytecode):
	return Web3.solidityKeccak(["bytes1", "address", "uint256", "bytes"], ["0xFF", _address, _salt, _hash_bytecode]).hex()

address = "0x73b0B632961365bB1887b5186acEE4027BCF27BA"
salt = 0 # One value that works for this particular contract is 3884297 (after around 45 min bruteforce)
bytecode = ""
hash_bytecode = Web3.keccak(hexstr=bytecode)

current_address = computeAddress(address, salt, hash_bytecode)

while not('badc0de' in current_address):
	print(current_address + " | salt = " + str(salt), end='\r')
	salt += 1
	current_address = computeAddress(address, salt, hash_bytecode)
print(current_address + " | salt = " + str(salt)) # Address is 0x37d56ebf7ab3c6fe7923e81c2d7c72cbadc0de938ee9b8b8455884f18a82d5b3
print('=== Found it ! ===') #																	^^^^^^^